import loading from '../svg/loading.svg'
import useFetch from '../hooks/useFetch';

const TodaysForecast = ({weatherIcons}) => {
    const { data: hours, isPending, error } = useFetch('http://localhost:8000/todaysForecast');

    return (  
        <div className='grid grid-rows-2 grid-cols-5 justify-center items-center w-600 bg-light-obsidian'>
            <h1 className='col-span-5'>Today's Forecast</h1>

            {isPending && <p>Loading...</p>}
            {error && <p>{error}</p>}
            {hours && hours.map((hour) => (
                <div className='flex flex-col'>
                    <div>{hour.time}</div>
                    <img src={weatherIcons[hour.weather]} alt="icon not found" title={hour.weather} className='h-40' />
                    <div>{hour.degree}</div>
                </div>
            ))}
        </div>
    );
}
 
export default TodaysForecast;
