import TodaysForecast from "./TodaysForecast";

const Forecast = ({weatherIcons}) => {
    

    return (  
        <div className="justify-self-start self-start grid grid-rows-2 h-600 gap-3">
            {/* window for forecast */}
            <div>
                {/* city and forecast */}
                <div className="grid grid-cols-2 grid-rows-2 h-200 w-700">
                    <div className="self-start justify-self-start">
                        <div className="text-6xl">Tbilisi</div>
                        <div className="text-3xl mt-2">Sunny</div>
                    </div>
                    
                    
                    <img src={weatherIcons.Sunny}  alt="icon not found" className="row-span-2 self-center justify-self-center h-320 w-320"/>

                    <div className="text-5xl self-end justify-self-start">31°</div>
                </div>
            </div>
            <TodaysForecast weatherIcons={weatherIcons} />
        </div>
    );
}

export default Forecast;

