import TodaysForecast from "./TodaysForecast";
import DetailedForecast from "./DetailedForecast";
import SevenDayForecast from "./SevenDayForecast";
import sunny from '../svg/sunny.svg';
import cloudy from '../svg/cloudy.svg';
import rainy from '../svg/rainy.svg';
import sunnyRainy from '../svg/sunnyRainy.svg';
import thunder from '../svg/thunder.svg';


const Forecast = () => {
    const weatherIcons = {
        Sunny: sunny,
        Cloudy: cloudy,
        Rainy: rainy,
        "Partly Cloudy": sunnyRainy,
        Thunderstorms: thunder,
    };

    return (  
        <div className="justify-self-start self-start">
            {/* window for forecast */}
            <div>
                {/* city and forecast */}
                <div className="grid grid-cols-2 grid-rows-2 h-300 w-800">
                    <div className="self-start justify-self-start">
                        <div className="text-6xl">Tbilisi</div>
                        <div className="text-3xl mt-2">Sunny</div>
                    </div>
                    
                    
                    <img src={weatherIcons.Sunny}  alt="icon not found" className="row-span-2 self-center justify-self-center h-100"/>

                    <div className="text-5xl self-end justify-self-start">31°</div>
                </div>

                {/* <TodaysForecast weatherIcons={weatherIcons} />
                <DetailedForecast />
                <SevenDayForecast weatherIcons={weatherIcons} /> */}
            </div>
        </div>
    );
}
 
export default Forecast;

