import TodaysForecast from "./TodaysForecast";
import DetailedForecast from "./DetailedForecast";
import SevenDayForecast from "./SevenDayForecast";
import sunny from '../svg/sunny.svg';
import cloudy from '../svg/cloudy.svg';
import rainy from '../svg/rainy.svg';
import sunnyRainy from '../svg/sunnyRainy.svg';
import thunder from '../svg/thunder.svg';


const Forecast = () => {
    const weatherIcons = {
        Sunny: sunny,
        Cloudy: cloudy,
        Rainy: rainy,
        "Partly Cloudy": sunnyRainy,
        Thunderstorms: thunder,
    };

    return (  
        <div >
            {/* window for forecast */}
            <div className="grid grid-cols-2 grid-rows-3 d-n">
                {/* city and forecast */}
                <div>
                    <div>Tbilisi</div>
                    <div>Sunny</div>
                    
                    <img src={weatherIcons.Sunny}  alt="icon not found" />

                    <div>31°</div>
                </div>

                <TodaysForecast weatherIcons={weatherIcons} />
                <DetailedForecast />
                <SevenDayForecast weatherIcons={weatherIcons} />
            </div>
        </div>
    );
}
 
export default Forecast;

