import useFetch from "../hooks/useFetch";

const SevenDayForecast = ({weatherIcons}) => {
    const { data: days, isPending, error } = useFetch('http://localhost:8000/sevenDayForecast');

    return (  
        <div className="row-span-3 self-start mt-40 bg-light-obsidian w-400 h-600 flex flex-col justify-center items-center">
            <h1 className=" self-start ">7-Day Forecast</h1>

            {isPending && <p>Loading...</p>}
            {error && <p>{error}</p> }
            {days && days.map((day) => ( 
                <div className="grid grid-cols-3 justify-center items-center  w-300">
                    <h4 className="justify-self-start">{day.day}</h4>
                    <div className="justify-self-end flex flex-col justify-center items-center">
                        <img src={weatherIcons[day.weather]} alt="icon not found" className="h-60"/>
                        <h4>{day.weather}</h4>
                    </div>
                    <h4 className="justify-self-end">{day.degree}</h4>
                </div>
            ))}
        </div>
    );
}
 
export default SevenDayForecast;
