import loading from '../svg/loading.svg'
import useFetch from '../hooks/useFetch';

const TodaysForecast = ({weatherIcons}) => {
    const { data: hours, isPending, error } = useFetch('http://localhost:8000/todaysForecast');

    return (  
        <div className='w-600 h-200 bg-light-obsidian grid grid-rows-body gap-10 justify-center items-center p-10 rounded-2xl'>
            <h1>Today's Forecast</h1>

            {isPending && <p>Loading...</p>}
            {error && <p>{error}</p>}
            <div className='w-500 grid grid-cols-5 justify-center items-center gap-5'>
                {hours && hours.map((hour) => (
                    <div className='flex flex-col justify-center items-center'>
                        <div>{hour.time}</div>
                        <img src={weatherIcons[hour.weather]} alt="icon not found" title={hour.weather}  />
                        <div>{hour.degree}</div>
                    </div>
                ))}
            </div>
        </div>
    );
}
 
export default TodaysForecast;
